var pdfMake = require("pdfmake/build/pdfmake.js");
var pdfFonts = require("pdfmake/build/vfs_fonts.js");
pdfMake.vfs = pdfFonts.pdfMake.vfs;

const jsPdfGenerator = async ({ logo, name, email, phoneNumber, heading, details, t = (text) => text }) => {
  const dd = {
    pageMargins: [40, 80, 40, 30],
    header: {
      columns: [
        {
          image: logo,
          width: 50,
          margin: [10, 10],
        },
        {
          text: name,
          margin: [20, 25],
          fontSize: 14,
          bold: true,
        },
        {
          text: email,
          margin: [165, 25, 0, 25],
          fontSize: 11,
          color: "#464747",
        },
        {
          text: phoneNumber,
          color: "#6f777c",
          fontSize: 11,
          margin: [-70, 45, 0, 25],
        },
      ],
    },

    footer: function (currentPage, pageCount) {
      return {
        columns: [
          { text: `${name} / ${heading}`, margin: [15, 0, 0, 0], fontSize: 11, color: "#6f777c", width: 400 },
          { text: `Page ${currentPage}`, alignment: "right", margin: [0, 0, 25, 0], fontSize: 11, color: "#6f777c" },
        ],
      };
    },
    content: [
      {
        text: heading,
        fontSize: 24,
        bold: true,
        margin: [-25, 5, 0, 0],
      },
      ...createContent(details, phoneNumber),
      {
        text: t("PDF_SYSTEM_GENERATED_ACKNOWLEDGEMENT"),
        fontSize: 11,
        color: "#6f777c",
        margin: [-25, 32],
      },
    ],
  };

  pdfMake.createPdf(dd).open();
};

export default { generate: jsPdfGenerator };

function createContent(details, phoneNumber) {
  const data = [];

  details.forEach((detail, index) => {
    let column1 = [];
    let column2 = [];

    if ((index + 1) % 7 === 0) {
      data.push({
        text: "",
        margin: [-25, 0, 0, 200],
      });
    }

    data.push({
      text: `${detail.title}`,
      fontSize: 18,
      bold: true,
      margin: [-25, 20, 0, 20],
    });

    const newArray = [];
    let count = 0;
    let arrayNumber = 0;

    detail.values.forEach((value, index) => {
      if (count <= 3) {
        if (!newArray[arrayNumber]) {
          newArray[arrayNumber] = [];
        }
        if (value) {
          newArray[arrayNumber].push(value);
        }
        count++;
      }
      if (count === 4) {
        count = 0;
        arrayNumber++;
      }
    });

    newArray.forEach((value) => {
      if (value?.length === 2) {
        createContentForDetailsWithLengthOfTwo(value, data, column1, column2, detail.values.length > 3 ? 10 : 0);
      } else if (value?.length === 1 || value?.length === 3) {
        createContentForDetailsWithLengthOfOneAndThree(value, data, column1, column2, detail.values.length > 3 ? 10 : 0);
      } else {
        value.forEach((value, index) => {
          let margin = [-25, 0, 0, 5];
          if (index === 1) margin = [15, 0];
          if (index === 2) margin = [30, 0];
          if (index === 3) margin = [20, 0];
          column1.push({
            text: value.title,
            fontSize: 11,
            bold: true,
            margin,
          });
          if (index === 1) margin = [15, 0, 0, 5];
          if (index === 2) margin = [30, 0, 0, 5];
          if (index === 3) margin = [20, 0, 0, 5];
          column2.push({
            text: value.value,
            fontSize: 9,
            margin,
            color: "#1a1a1a",
          });
        });
        data.push({ columns: column1 });
        data.push({ columns: column2 });
        column1 = [];
        column2 = [];
      }
    });
  });

  return data;
}

function createContentForDetailsWithLengthOfTwo(values, data, column1, column2, num = 0) {
  values.forEach((value, index) => {
    if (index === 0) {
      column1.push({
        text: value.title,
        fontSize: 12,
        bold: true,
        margin: [-25, num - 10],
      });
      column2.push({
        text: value.value,
        fontSize: 9,
        margin: [-25, 5, 0, 0],
        color: "#1a1a1a",
      });
    } else {
      column1.push({
        text: value.title,
        fontSize: 12,
        bold: true,
        margin: [-115, num - 10],
      });
      column2.push({
        text: value.value,
        fontSize: 9,
        margin: [-115, 5, 0, 0],
        color: "#1a1a1a",
      });
    }
  });
  data.push({ columns: column1 });
  data.push({ columns: column2 });
}

function createContentForDetailsWithLengthOfOneAndThree(values, data, column1, column2, num = 0) {
  values.forEach((value, index) => {
    if (index === 0) {
      column1.push({
        text: value.title,
        fontSize: 12,
        bold: true,
        margin: values.length > 1 ? [-25, -5, 0, 0] : [-25, 0, 0, 0],
      });
      column2.push({
        text: value.value,
        fontSize: 9,
        color: "#1a1a1a",
        margin: values.length > 1 ? [-25, 5, 0, 0] : [-25, 5, 0, 0],
      });
    } else if (index === 2) {
      column1.push({
        text: value.title,
        fontSize: 12,
        bold: true,
        margin: [-60, -5, 0, 0],
      });
      column2.push({
        text: value.value,
        fontSize: 9,
        margin: [-60, 5, 0, 0],
        color: "#1a1a1a",
      });
    } else {
      column1.push({
        text: value.title,
        fontSize: 12,
        bold: true,
        margin: [-28, -5, 0, 0],
      });
      column2.push({
        text: value.value,
        fontSize: 9,
        margin: [-28, 5, 0, 0],
        color: "#1a1a1a",
      });
    }
  });
  data.push({ columns: column1 });
  data.push({ columns: column2 });
}

